import React from 'react';
import styled from 'styled-components';
import PinterestIcon from '@material-ui/icons/Pinterest';
import IconButton from '@material-ui/core/IconButton';
import SearchIcon from '@material-ui/icons/Search';




export const Header = () => {
    return (
        <Wrapper>
            <LogoWrapper>
                <IconButton>
                    <PinterestIcon />
                </IconButton>
            </LogoWrapper>
            <HomePageButton>
                <a href="/">Homepage</a>
            </HomePageButton>

            <FollowingButton>
                <a href="/">Following</a>
            </FollowingButton>

            <SearchWrapper>
                <SearchBarWrapper>
                    <IconButton>
                        <SearchIcon />
                    </IconButton>
                    <form>
                        <input type='text' />
                        <button type="submit"></button>
                    </form>
                </SearchBarWrapper>

            </SearchWrapper>
            {/*    
            <IconsWrapper>


            </IconsWrapper> */}
        </Wrapper>
    );
};

export default Header;

const Wrapper = styled.div`
display:flex;
align-items:center;
height: 56px;
padding: 12px 4px 4px 16px;
background-color:white;
color:black;
`;

const LogoWrapper = styled.div`
.MuiSvgIcon-root {
    color: #eb090f;
    font-size: 32px;
    cursor:pointer;
}
`;

const HomeButtons = styled.div`
    display:flex;
    align-items:center;
    justify-content: center;
    height: 48px;
    min-width: 123px;
    border-radius: 24px;
    cursor: pointer;
`;

const HomePageButton = styled(HomeButtons)`

    background-color: rgb(17,17,17);

    a{
        text-decoration: none;
        color: white;
        font-weight: 700;
    }
`;

const FollowingButton = styled(HomeButtons)`

    background-color: white;

    a{
        text-decoration: none;
        color: black;
        font-weight: 700;
    }

    :hover{
        background-color: #e1e1e1;
    }
`;

const SearchWrapper = styled.div`
flex: 1;
`;

const SearchBarWrapper = styled.div`
    background-color: #efefef;
    display:flex;
    height:48px;
    width: 100%;
    border-radius:50px;
    border:none;
    padding-left:10px;

    form{
        display: flex;
        flex: 1;
    }

    form > input {
        background-color: transparent;
        border:none;
        width: 100%;
        margin-left: 5px;
        font-sized: 16px;
    }

    form > button {
        display: none;
    }

    input: focus {
        outline: none;
    }
`;
